import { Component, OnInit/*, OnChanges, SimpleChanges*/ } from '@angular/core';
import { ApiService } from './api.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'my-app';

  constructor(private apiService: ApiService) {  }

  limitPersonas: number = 5;
  juegos: any[] = [];
  jsonData: any[] = [];//JSON.parse('[{"Nombre":"Usuario1","Puntaje":370,"DireccionFoto":"assets/fotoperfil.jpeg"},{"Nombre":"Usuario2","Puntaje":360,"DireccionFoto":"assets/fotoperfil.jpeg"},{"Nombre":"Usuario3","Puntaje":340,"DireccionFoto":"assets/fotoperfil.jpeg"},{"Nombre":"Usuario4","Puntaje":340,"DireccionFoto":"assets/fotoperfil.jpeg"},{"Nombre":"Usuario5","Puntaje":250,"DireccionFoto":"assets/fotoperfil.jpeg"}]'); // Variable para almacenar el JSON
  //jsonDataCount: number = 0; // Variable para almacenar la cantidad de elementos en el JSON

  ngOnInit() {
    // Aquí deberías cargar tu JSON, por ejemplo, desde un servicio HTTP
    this.loadJuegos();
    this.loadJsonData();
  }

  loadJuegos(){
    this.apiService.getPuntajesTotalesOrdenadosLimit(this.limitPersonas).subscribe(data => { 
      console.log(data);
      this.juegos = data;
    });
  }

  loadJsonData(){
    this.apiService.getPuntajesTotalesOrdenadosLimit(this.limitPersonas).subscribe(data => { 
      //console.log(data)
      this.jsonData = data; 
      //this.jsonDataCount = this.jsonData.length;
    });
  }

  onJuegoClick(juego: string): void {
    this.apiService.getPuntajesPorJuegoOrdenadosLimit(juego, this.limitPersonas).subscribe(data => { 
      this.jsonData = data;
      console.log(data);
    });
  }

